/*
 * @brief API to retrieve the version of the HTTP_SERVER APP.
 *
 * @return DAVE_APP_VERSION_t Structure containing major version, minor version
 *         and patch version.
 */
DAVE_APP_VERSION_t HTTP_SERVER_GetAppVersion(void)
{
  DAVE_APP_VERSION_t version;

  version.major = HTTP_SERVER_MAJOR_VERSION;
  version.minor = HTTP_SERVER_MINOR_VERSION;
  version.patch = HTTP_SERVER_PATCH_VERSION;

  return version;
}

/*
 * @brief API to initialize the HTTP server software stack.
 *
 * @param[in]  handle HTTP_SERVER APP handle pointer of type HTTP_SERVER_t*
 *
 * @return  HTTP_SERVER_STATUS_t
 *          HTTP_SERVER_SUCCESS: for successful LWIP initialization.<BR>
 *          HTTP_SERVER_STATUS_FAILURE  : If LWIP initialization fails.<BR>
 *
 */
HTTP_SERVER_STATUS_t HTTP_SERVER_Init(HTTP_SERVER_t* handle)
{
  HTTP_SERVER_STATUS_t status = HTTP_SERVER_STATUS_SUCCESS;
  /*Initialize LWIP*/
  status = (HTTP_SERVER_STATUS_t)ETH_LWIP_Init(handle->lwip);
#if HTTPD_FAT_FS
  if (status == HTTP_SERVER_STATUS_SUCCESS)
  {
    /*Initialize FATFS*/
  status = (HTTP_SERVER_STATUS_t)FATFS_Init(handle->fatfs);
  }
#endif

#if 0
  if (status == HTTP_SERVER_STATUS_SUCCESS)
  {
    /*Initialize tcp state for handling HTTP*/
    if (true != httpd_init())
    {
      status = HTTP_SERVER_STATUS_FAILURE;
    }
  }
#endif

  return status;
}
